import { API } from "@/services/endPoints";
import { {moduleName}ApiResponse } from "../{moduleName.toLowerCase()}.types";
import { QueryParams } from "@/types/api-request";
import {
  useDeleteData,
  useFetchData,
  useFetchDetails,
  usePostData,
  useUpdateData,
} from "@/hooks/api-request";

export const use{moduleName}List = (params: QueryParams) => {
  return useFetchData<{moduleName}ApiResponse>({
    url: `${API.{moduleName.toLowerCase()}.list}`,
    params: params,
  });
};

export const useCreate{moduleName} = () => {
  return usePostData({
    url: `${API.{moduleName.toLowerCase()}.add}`,
    mutationOptions: {
      onSuccess: (data) => {
        console.log("{moduleName} added successfully", data);
      },
      onError: (error) => {
        console.log("{moduleName} addition failed", error);
      },
    },
  });
};

export const useUpdate{moduleName} = () => {
  return useUpdateData({
    url: `${API.{moduleName.toLowerCase()}.update}`,
    mutationOptions: {
      onSuccess: (data) => {
        console.log("{moduleName} updated successfully", data);
      },
      onError: (error) => {
        console.log("{moduleName} update failed", error);
      },
    },
  });
};

export const useDelete{moduleName} = () => {
  return useDeleteData({
    url: `${API.{moduleName.toLowerCase()}.delete}`,
    mutationOptions: {
      onSuccess: (data) => {
        console.log("{moduleName} deleted successfully", data);
      },
      onError: (error) => {
        console.log("{moduleName} deletion failed", error);
      },
    },
  });
};

export const use{moduleName}Details = (id: number) => {
  return useFetchDetails({
    url: API.{moduleName.toLowerCase()}.details,
    id,
    queryOptions: {},
  });
};
