import { buildQueryString } from "@/lib/common-functions";
import { API } from "@/services/endpoints";
import superAxios from "@/services/instance";
import { QueryParams } from "@/services/types/params";
import { Add{moduleName}Payload, Edit{moduleName}Payload } from "@/services/types/payload";

export const get{moduleName}ListApi = async (params: QueryParams): Promise<any> => {
  const queryString = buildQueryString(params);
  const response = await superAxios.get(`${API.{moduleName.toLowerCase()}.list}${queryString}`);
  if (response.status == 200) {
    return response?.data?.data;
  } else {
    return false;
  }
};

export const add{moduleName}Api = async (payload: Add{moduleName}Payload): Promise<any> => {
  const response = await superAxios.post(`${API.{moduleName.toLowerCase()}.add}`, payload);
  if (response.data?.status) {
    return response?.data;
  } else {
    return false;
  }
};

export const getDataById{moduleName}Api = async (id: number): Promise<any> => {
  const response = await superAxios.get(`${API.{moduleName.toLowerCase()}.{moduleName.toLowerCase()}listgetbyid}${id}`);
  if (response.data?.status) {
    return response?.data?.data;
  } else {
    return false;
  }
};

export const update{moduleName}Api = async (
  id: number,
  payload: Edit{moduleName}Payload
): Promise<any> => {
  const response = await superAxios.patch(`${API.{moduleName.toLowerCase()}.update}${id}`, payload);
  if (response.data?.status) {
    return response?.data;
  } else {
    return false;
  }
};

export const {moduleName}DeleteByIdApi = async (id: number): Promise<any> => {
  const response = await superAxios.delete(`${API.{moduleName.toLowerCase()}.delete}${id}`);
  if (response.data?.status) {
    return response?.data?.data;
  } else {
    return false;
  }
};
